const keywords = ["personal finance tips","crypto regulations","mobile money","stock market news","AI trading bots","financial literacy","best savings account","forex rates","AI productivity tools","best smartphones","VR headsets","wearable technology","cloud computing","cybersecurity news","tech gadgets","electric cars","small business ideas","remote work","startup funding","entrepreneurship tips","e-commerce trends","digital marketing","supply chain","business automation","funny memes","office humor","work memes","AI generated memes","funny quotes","viral jokes","meme trends","funny TikTok","cheap flights","solo travel","eco-friendly travel","travel hacks","digital nomad","luxury travel","hidden gems travel","travel safety"];
const title = $json["title"].toLowerCase();
if (keywords.some(k => title.includes(k))) {
  return [{ json: $json }];
}
return [];

const keyword = $json["title"].toLowerCase();
let topic = '';
if (["personal finance tips","crypto regulations"].some(k => keyword.includes(k))) {
  topic = 'Finance';
} else if (["AI productivity tools"].some(k => keyword.includes(k))) {
  topic = 'Tech';
} else if (["small business ideas"].some(k => keyword.includes(k))) {
  topic = 'Business';
} else if (["funny memes","office humor"].some(k => keyword.includes(k))) {
  topic = 'Humor';
} else if (["cheap flights","solo travel"].some(k => keyword.includes(k))) {
  topic = 'Travel';
}
return [{ json: { topic, keyword: $json["title"], link: $json["link"], relatedQueries: '', status: 'Idea', dateFound: new Date() } }];
